I think the following might work, but I haven't had a chance to test it yet

~~~
diff -r f72d725c02ac gazebo/physics/Joint.cc
--- a/gazebo/physics/Joint.cc	Fri Sep 02 21:30:48 2016 -0700
+++ b/gazebo/physics/Joint.cc	Fri Sep 02 21:38:19 2016 -0700
@@ -520,6 +520,12 @@
   for (unsigned int i = 0; i < this->GetAngleCount(); ++i)
   {
     this->SetVelocity(i, 0.0);
+    // clamp initial position between joint limits
+    ignition::math::Angle position = ignition::math::clamp(
+      this->InitialPosition(i),
+      this->GetLowerLimit(i).Ign(),
+      this->GetUpperLimit(i).Ign());
+    this->SetPosition(i, position.Radian());
   }
   this->staticAngle.SetFromRadian(0);
 }
~~~
