one more nitpick: I think we could use specialized comparison expectations for the floating point numbers:

~~~
--- a/test/plugins/PluginInterfaceTest.cc	2018-12-13 14:09:30.616733008 -0800
+++ b/test/plugins/PluginInterfaceTest.cc	2018-12-13 13:44:38.991254142 -0800
@@ -89,20 +89,20 @@
     float f = 3.14f;

     LoadParam(_sdf, "float", f, 0.0f);
-    EXPECT_NEAR(f, 3.14f, 0.0f);
+    EXPECT_FLOAT_EQ(f, 3.14f);

     LoadParam(_sdf, "float_nonexistent", f, 1.0f);
-    EXPECT_NEAR(f, 1.0f, 0.0f);
+    EXPECT_FLOAT_EQ(f, 1.0f);
   }

   {
     double d = 3.14;

     LoadParam(_sdf, "float", d, 0.0);
-    EXPECT_NEAR(d, 3.14, 0.0);
+    EXPECT_DOUBLE_EQ(d, 3.14);

     LoadParam(_sdf, "float_nonexistent", d, 1.0);
-    EXPECT_NEAR(d, 1.0, 0.0);
+    EXPECT_DOUBLE_EQ(d, 1.0);
   }

   {
~~~
